{
    "name": "Paris Trip Planner",
    "nodes": [
        {
            "id": "agent_city_expert",
            "type": "agent",
            "position": {"x": 100, "y": 100},
            "data": {
                "role": "Local Travel Expert",
                "goal": "Provide detailed information about a given city, including key attractions, local customs, and best times to visit.",
                "backstory": "You are an AI assistant with a passion for travel and extensive knowledge about cities around the world. You use web search to find the most up-to-date information.",
                "verbose": true,
                "allow_delegation": false
            }
        },
        {
            "id": "tool_search",
            "type": "tool",
            "position": {"x": 100, "y": 250},
            "data": {
                "tool_id": "serper_dev_tool",
                "config_params": {}
            }
        },
        {
            "id": "agent_itinerary_planner",
            "type": "agent",
            "position": {"x": 100, "y": 400},
            "data": {
                "role": "Itinerary Planning Specialist",
                "goal": "Create a well-structured and engaging travel itinerary based on provided city information and duration.",
                "backstory": "You are an AI that excels at crafting personalized travel plans, balancing sightseeing, cultural experiences, and relaxation.",
                "verbose": true,
                "allow_delegation": true 
            }
        },
        {
            "id": "task_research_paris",
            "type": "task",
            "position": {"x": 350, "y": 100},
            "data": {
                "description": "Research Paris, France. Find key attractions (top 5), a brief note on local customs (e.g., tipping), and the best months to visit for pleasant weather.",
                "expected_output": "A summary report about Paris including: Top 5 attractions, a note on local customs, and best months to visit."
            }
        },
        {
            "id": "task_plan_paris_trip",
            "type": "task",
            "position": {"x": 350, "y": 400},
            "data": {
                "description": "Based on the research for Paris, create a 3-day travel itinerary. The itinerary should include a mix of famous landmarks and local experiences. Suggest morning, afternoon, and evening activities for each day.",
                "expected_output": "A detailed 3-day itinerary for Paris, with suggested activities for morning, afternoon, and evening each day."
            }
        }
    ],
    "edges": [
        {"id": "e1", "source": "tool_search", "target": "agent_city_expert"},
        {"id": "e2", "source": "agent_city_expert", "target": "task_research_paris"},
        {"id": "e3", "source": "task_research_paris", "target": "task_plan_paris_trip"}, 
        {"id": "e4", "source": "agent_itinerary_planner", "target": "task_plan_paris_trip"}
    ]
}
